    {
    _id: "1a",
    name: "Task1",
    description: "Do something important",
  },
  {
    _id: "2b",
    name: "Task2",
    description: "Do something important",
  },
  {
    _id: "3c",
    name: "Task3",
    description: "Do something important",
  },
];

function ToDoList() {
  const [tasks, setTasks] = useState(initialTasks);

  return (
    <div>
      <Summary />

      <div className="todo-container">
        {tasks.map((task) => <Task key={task._id} task={task} /> )}
      </div>
    </div>
  );
}

export default ToDoList;
